	public static int maxPerformance(int n, int[] speed, int[] efficiency, int k) {
        List<int[]> engList = new ArrayList<>();
        for(int i=0; i<speed.length; i++) {
            engList.add(new int[]{speed[i], efficiency[i]});
        }
        
        engList.sort((a,b) -> b[1]-a[1]);
        PriorityQueue<Integer> pq = new PriorityQueue<>((a,b) -> a-b);
        long sumOfSpeed = 0;
        long maxPerf = 0;
        for(int[] eng: engList) {
            sumOfSpeed+=eng[0];
            pq.offer(eng[0]);
            if(pq.size()>k) {
                sumOfSpeed-=pq.poll();
            }
            maxPerf = Math.max(maxPerf, sumOfSpeed*eng[1]);
        }
        // the mod is applied as per the question to handle large maxPer value;
        return (int) (maxPerf % (Math.pow(10, 9) + 7));
    }
